import React, { useEffect, useRef, useState } from 'react'
import useSetInterval from '../../hooks/useSetInterval'
import frame1 from './../../../imgs/pear-frame-1.svg'
import frame2 from './../../../imgs/pear-frame-2.svg'
import frame3 from './../../../imgs/pear-frame-3.svg'
import './styles.scss'

function Narrator() {

    const frameSrcs = [frame1, frame2, frame3]
    const [frameIdx, setFrameIdx] = useState(0)
    const lastFrameIdx = useRef(0)

    useEffect(() => {
        console.log(frameIdx)
    }, [frameIdx])

    const changeAnimationFrame = () => {
        switch (frameIdx) {
            case 0:
                setFrameIdx(1)
                lastFrameIdx.current = 0
                break
            case 1:
                // setFrameIdx(lastFrameIdx.current === 0 ? 2 : 0 )
                setFrameIdx(2)
                lastFrameIdx.current = 1
                break
            case 2:
                setFrameIdx(1)
                lastFrameIdx.current = 2
                break
            default:
        }

    }

    useSetInterval(changeAnimationFrame, 500, [])

    return (
        <img src={frameSrcs[frameIdx]} id='narrator' />
    )
}

export default Narrator
