import React, { useEffect } from 'react'

function useSetInterval({ fn, time, dependencies }) {

    useEffect(() => {
        const interval = setInterval(() => {
            fn()
            console.log('inter')
        }, time)

        return () => {
            clearInterval(interval)
        }

    }, [])
}

export default useSetInterval
