import React, { useEffect, useRef } from 'react'

function useAudioInAnimation(audioSrc, callback) {


    useEffect(() => {

        const audio = new Audio(`./../audios/${audioSrc}.wav`);

        audio.play()
        audio.addEventListener('ended', callback);

        return () => {
          audio.removeEventListener('ended', callback);
        };
      }, []);
}

export default useAudioInAnimation
