import React, { useEffect, useRef } from 'react'
import { useDispatch } from 'react-redux'
import { callNextAnimationFrame } from '../redux/animation'

function useAudioInAnimation(audioSrc) {

    const dispatch = useDispatch()
    const audioRef = useRef()


    useEffect(() => {

        audio.current.play()
        audio.current.addEventListener('ended', handleAudioEnding)

        return () => {
            audio.current.removeEventListener('ended', handleAudioEnding)
        }
    }, [])

    function handleAudioEnding() {
        dispatch(callNextAnimationFrame())
    }

    return (
        <audio autoPlay={true} ref={audioRef}>
            <source src={audioSrc} type="audio/wav" />
        </audio>
    )
}

export default useAudioInAnimation
