import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import './styles.scss'

function Quiz() {

    const [round, setRound] = useState(0)
    const [quizOptions, setQuizOptions] = useState([])

    const carbs = useSelector((state) => state.foods.carbs)
    const prots = useSelector((state) => state.foods.prots)
    const fats = useSelector((state) => state.foods.fats)

    useEffect(() => {
        function includeRandomFoodInQuizOptions(foodsArray) {
            const quizOptionsLengthBeforePush = quizOptions.length

            while (quizOptions.length !== quizOptionsLengthBeforePush + 1) {
                const randomFood = foodsArray[Math.floor(Math.random() * foodsArray.length)]
                setQuizOptions((state) => [...state, randomFood])
            }
        }

        includeRandomFoodInQuizOptions(carbs)
        includeRandomFoodInQuizOptions(carbs)
        includeRandomFoodInQuizOptions(carbs)

        while (quizOptions.length !== 3) {
        }

        console.log(carbs[Math.floor(Math.random() * carbs.length)])

    }, [])


    const isItProteinRound = () => (round === 0 || round === 1)
    const isItCarbRound = () => (round === 2 || round === 3)
    const isItFatRound = () => (round === 4)

    return (
        <article id="quiz">
            <h1>
                Quais desses alimentos contém muitas
                {isItProteinRound() && 'proteínas'}
                {isItCarbRound() && 'carboidratos'}
                {isItFatRound() && 'gorduras'}
                ? 
            </h1>

            <div id='quiz-options'>
                <img src={fats[0]} alt='Gordura 1' className='icon' />
            </div>

        </article>
    )
}

export default Quiz
