import React, { useEffect, useRef } from 'react'

function useAudioInAnimation(audioRef, callback) {


    useEffect(() => {

        audioRef.play()
        audioRef.addEventListener('ended', callback)

        return () => {
          audioRef.removeEventListener('ended', callback)
        }
      }, [])
}

export default useAudioInAnimation
