import React, { useEffect, useState } from 'react'
import useSetInterval from '../../hooks/useSetInterval'
import frame1 from './../../../imgs/pear-frame-1.svg' 
import frame2 from './../../../imgs/pear-frame-2.svg' 
import frame3 from './../../../imgs/pear-frame-3.svg' 
import './styles.scss'

function Narrator() {

    const frameSrcs = [frame1, frame2, frame3]
    const [frameIdx, setFrameIdx] = useState(0)

    useEffect(() => {
        console.log(frameIdx)
    }, [frameIdx])

    useSetInterval(changeAnimationFrame, 400, [frameIdx])

    function changeAnimationFrame() {
        switch (frameIdx) {
            case 0:
            case 1:
                setFrameIdx((state) => state + 1)
                break;
            case 2:
                setFrameIdx(0)
                break;
            default:
        }

    }

    return (
        <img src={frameSrcs[frameIdx]} id='narrator' />
    )
}

export default Narrator
