import React, { useEffect, useRef } from 'react'

function useAudioInAnimation(audioSrc, callback) {

    const audio = useRef(new Audio(`./../audios/${audioSrc}.wav`))


    useEffect(() => {

        audio.muted = false
        audio.current.play()
        audio.current.addEventListener('ended', callback)

        return () => {
          audio.current.removeEventListener('ended', callback)
        }
      }, [])

      return <button>ai</button>
}

export default useAudioInAnimation
