import React, { useEffect } from 'react'

function useSetInterval(fn, time, dependencies) {

    useEffect(() => {
        const interval = setInterval(fn, time)

        return () => {
            console.log('asd')
            clearInterval(interval)
        }

    }, [])
}

export default useSetInterval
