import React, { useEffect, useRef } from 'react'

function useAudioInAnimation(audioSrc, callback) {

    const gameMode = useSelector(state => state.game.gameMode)
    const audioRef = useRef()


    useEffect(() => {

        audio.current.play()
        audio.current.addEventListener('ended', callback)

        return () => {
          audio.current.removeEventListener('ended', callback)
        }
      }, [])

    return (
        <audio autoPlay={true} ref={audioRef}>
            <source src={audioSrc} type="audio/wav" />
        </audio>
    )
}

export default useAudioInAnimation
