import React, { useState } from 'react'

const formStyle = {
    display: 'flex',
    flexWrap: 'wrap',
    flexDirection: 'column',
    justifyContent: 'space-between',

    margin: '20vh auto 0 auto',
    height: '28%',
    width: '45%',

}

const buttonContainerStyles = {

    display: 'flex',
    justifyContent: 'space-between',

}

const buttonStyles = {
    width: '13vw',
    margin: '0',
    justifySelf: 'end',
    alignSelf: 'flex-end',
}

function useForm(btn1Txt, btn1Callback, btn2Txt, btn2Callback) {

    const [input1Value, setinput1Value] = useState()
    const [btn2Value, setBtn2Value] = useState()

    const formJsx = (
        <div id='form' style={formStyle}>
            <div>
                <InputGroup className='mb-3'>
                    <InputGroup.Text id='inputGroup-sizing-default'>
                        E-mail
                    </InputGroup.Text>
                    <Form.Control
                        aria-label='Default'
                        aria-describedby='inputGroup-sizing-default'
                        value={input1Value}
                        onChange={(e) => setinput1Value(e.target.value)}
                    />
                </InputGroup>

                <InputGroup className='mb-3'>
                    <InputGroup.Text id='inputGroup-sizing-default'>
                        Senha
                    </InputGroup.Text>
                    <Form.Control
                        aria-label='Default'
                        aria-describedby='inputGroup-sizing-default'
                        value={btn2Value}
                        onChange={(e) => setBtn2Value(e.target.value)}
                    />
                </InputGroup>
            </div>

            <div id='button-container' style={buttonContainerStyles}>
                <Button variant='light' style={buttonStyles}
                    onClick={btn1Callback}>
                    {btn1Txt}
                </Button>

                <Button variant='success' style={buttonStyles}
                    onClick={btn2Callback}>
                    {btn2Txt}
                </Button>
            </div>
        </div>
    )

    return [formJsx, input1Value, btn2Value]
}

export default useForm
